import React, { Component } from 'react';
import styled from '@emotion/styled';
import honeyjar from '../Images/honeyjar.png';

const DisplayArea = styled.div`

  margin: 0 auto 30vh auto;
  width: 100%;
  max-width: 750px;
  margin-top: 200px;
  @media(max-width: 768px) {
    width: 96%;
    left: 1%;
    margin: 0 auto;
  }
`;

const Title = styled.div`

  font-size: 42px;
  font-weight: bold;
  border-bottom-style: solid;
  border-bottom-width: 2px;
  border-color: black;
  width: 30%;
  text-align: left;
  margin: 0 4px;
  padding: 0 4px;

  @media(max-width: 768px) {
    font-size: 8vw;
    text-align: center;
    margin: 0px auto;
    width: 70%;
  }

`;
const Description = styled.div`
  font-size: 24px;

  width: 100%;
  text-align: left;
  margin: 0 4px;
  padding: 0 4px;

  @media(max-width: 768px) {
    font-size: 8vw;
    text-align: center;
    margin: 0px auto;
    width: 70%;
  }

`;


class About extends Component {
  render() {
    return (
      <React.Fragment>
        <DisplayArea>
          <Title>The Story</Title>
          <Description>
            Born in 1937 Papa Cecil became interested in beekeeping at a young age when his family 
            moved to a farm, and he discovered some discarded bee boxes. Because of the smell of 
            honey never leaves the wood, wild bees were attracted and had moved in to use the boxes 
            even though they were in disrepair. He started frequenting the local library and reading 
            National Geographic for any information he could get his hands on about raising bees. This 
            led to a lifetime hobby.<br/><br/>Later in life, he spent 8 years as the beekeeper at Red Butte Garden 
            for the University of Utah. Now he is retired to the Bear Lake Valley, where he grows flowers and 
            vegetables in his garden and raises bees in his spare time.
          </Description>
        </DisplayArea>
      </React.Fragment>
    );
  }
}
export default About;
