import React, { Component } from 'react';
import styled from '@emotion/styled';
import { NavLink } from 'react-router-dom'

const NavBar = styled.div`
  position: fixed;
  height: 35px;
  overflow: hidden;
  bottom: 0;
  width: 100%;
  background: blue;
`;
const NavItems = styled.div`
  text-align: center;
  margin: 0px auto;
  display: block;
`;
const NavItem = styled(NavLink)`
  text-decoration: none;
  color: black;
  display: inline-block;
  font-size: 36px;
  margin: 0 30px;
  margin-top: 35px;
  text-align: center;

  @media(max-width: 768px) {
    font-size: 8vw;
    margin: 0 7vw;
  }
  &:hover {
    border-bottom-style: solid;
    border-bottom-width: 2px;
    border-color: black;
    cursor: pointer;
  }
  &.active {
    border-bottom-style: solid;
    border-bottom-width: 2px;
    border-color: black;
  }
`;

class Nav extends Component {
  render() {
    return (
      <React.Fragment>
        <NavBar>
          <NavItems>
            <NavItem to={`/about`}>
                About
            </NavItem>
            <NavItem to={`/shop`}>
                Shop
            </NavItem>
            <NavItem to={`/faq`}>
                F.A.Q
            </NavItem>
          </NavItems>
        </NavBar>
      </React.Fragment>
    );
  }
}
export default Nav;
