import React, { Component } from 'react';
import styled from '@emotion/styled';
import { useRouteMatch, Navlink } from 'react-router-dom'

const NavBar = styled.div`
  position: fixed;
  background: #21272C;
  height: 100px;
  overflow: hidden;
  bottom: 0;
  width: 100%;
  box-shadow: 0px 0 25px 25px #21272C;
`;
const NavItems = styled.div`
  text-align: center;
  margin: 0px auto;
`;
const NavItem = styled(NavLink)`
  color: #FFFFFF;
  display: inline-block;
  font-size: 36px;
  margin: 0 15px;
  margin-top: 35px;
  text-decoration: none;
  @media(max-width: 768px) {
    font-size: 20px;
  }
  &:hover {
    color: #1DB954;
    cursor: pointer;
  }
  &.active {
    color: #1DB954;
  }
`;

class Nav extends Component {
  render() {
    return (
      <React.Fragment>
        <NavBar>
          <NavItems>
            <NavItem to={`/artists`}>

                Top Artists

            </NavItem>
            <NavItem to={`/tracks`}>

                Top Tracks

            </NavItem>
            <NavItem to={`/sort`}>

                Sort

            </NavItem>
          </NavItems>
        </NavBar>
      </React.Fragment>
    );
  }
}
