import React, { Component } from 'react';
import styled from '@emotion/styled';
import honeyjar from '../Images/honeyjar.png';

const DisplayArea = styled.div`

  margin: 0 auto 30vh auto;
  width: 100%;
  max-width: 750px;

  @media(max-width: 768px) {
    width: 96%;
    left: 1%;
  }
`;
const ProductImage = styled.img`
  float: left;
  width: 400px;
  margin: 0 0 40px 0;
`;
const ProductWording = styled.div`
  float: right;
  margin-top: 50px;
`;
const ProductTitle = styled.div`
  font-size: 42px;
  font-weight: bold;
  border-bottom-style: solid;
  border-bottom-width: 2px;
  border-color: black;
  width: 240px;
  text-align: left;
  margin: 0 4px;
  padding: 0 4px;

`;
const TitleFlavor = styled.div`
  font-size: 32px;
  color: grey;
  margin: 4px;
`;
const Description = styled.div`
  font-size: 24px;
  width: 270px;
  margin: 4px;
`;
const ButtonArea = styled.div`
  text-align: center;
`;
const Purchase = styled.button`
  text-decoration: none;
  border-bottom-style: solid;
  border-bottom-width: 2px;
  border-color: black;

`;
class Shop extends Component {
  render() {
    return (
      <React.Fragment>
        <DisplayArea>
          <ProductImage src={honeyjar}/>
          <ProductWording>
            <ProductTitle>Pure Honey</ProductTitle>
            <TitleFlavor>From bear Lake Valley</TitleFlavor>
            <Description>
              This honey is all natural, all roganic, raw unpasteurized mountain flower honey.
            </Description>
            <ButtonArea>
              <Purchase>Purchase</Purchase>
            </ButtonArea>

          </ProductWording>
        </DisplayArea>
      </React.Fragment>
    );
  }
}
export default Shop;
