import React, {useEffect} from 'react';
import styled from '@emotion/styled';
import { Link } from "react-scroll";
const List = styled.ul `
    

`;

const Item = styled.li`
    margin: 0;
    padding: 0;
    overflow: hidden;
    position: fixed;
    top: 0;
    width: 100%;
    text-decoration: none;
    float: right;
    display: block;
    color: black;
    text-align: center;
    font-size: 48px;
    padding: 28px 32px;
    text-decoration: none;
    border-radius: 50%;
    border-style: solid;
    border-color: black;
    height: 50px;
    width: 50px;
    padding: 10px;
    &:hover {
        background-color: black;
        color: white;
        cursor: pointer;
    }
`;

const Navbar=() => {


   return (
    <React.Fragment>
            <Link
                    activeClass="active"
                    to="about"
                    spy={true}
                    smooth={true}
                    offset={-70}
                    duration={500}>
                    <Item>^</Item>
                    </Link>
        </React.Fragment>
   )
};

export default Navbar;
