import React from 'react';
import require from 'react';
import styled from '@emotion/styled';

//Gets multiple Images
function importAll(r) {
  let images = {};
  r.keys().map((item, index) => { images[item.replace('./', '')] = r(item); });
  return images;
}

const images = importAll(require.context('../images', false, '/\.png/'));

const CardBorder = styled.div `
  border-radius: 40px;
  
  margin: 20px 20px 20px 20px;
  padding: 0.2rem;
  position: relative;
  width: 100vw;
  max-width: 350px;
  transition-duration: 0.8s;
  -webkit-transition-duration: 0.8s;
  background: transparent;
  &:hover {
    background-image: linear-gradient(to right bottom, #4fe8ff, #00caff, #00a7ff, #007eff, #0043ff);    box-shadow: 0 8px 16px 0 rgba(0,0,0,0.8);

  }
`;
const Card = styled.div `
  border-radius: 40px;
  background: white;
  transition-duration: 0.8s;
  -webkit-transition-duration: 0.8s;

  &:hover {
    //color: white;
    //background: transparent;
  }
`;
const Img = styled.img `
  border-top-left-radius: 40px;
  border-top-right-radius: 40px;
  width: 100%;
`;
const CardTitle = styled.div `
  font-size: 36px;
  padding: 0 10px 10px 10px;
`;
const CardDescription = styled.div `
  font-size: 24px;
  color: #6e6e6e;
  padding: 0 10px 15px 10px;
`;

function Cards() {  
  return (
    <React.Fragment>
      <CardBorder>
        <Card>
          <Img src={images['whattowear.png']}/>
          <CardTitle>WhatToWear</CardTitle>
          <CardDescription>
          The most fail-safe weather app.
          </CardDescription>
        </Card>
      </CardBorder>
      <CardBorder>
        <Card>
          <Img src={images['whattowear.png']}/>
          <CardTitle>WhatToWear</CardTitle>
          <CardDescription>
          The most fail-safe weather app.
          </CardDescription>
        </Card>
      </CardBorder>
      <CardBorder>
        <Card>
          <Img src={images['whattowear.png']}/>
          <CardTitle>WhatToWear</CardTitle>
          <CardDescription>
          The most fail-safe weather app.
          </CardDescription>
        </Card>
      </CardBorder>
    </React.Fragment>
    
  );
}

export default Cards;
