import React from 'react';
import styled from '@emotion/styled'
import whattowear from '../Images/whattowear.png'
import { Switch,
  Route,
  Link,
  NavLink,
} from 'react-router-dom';


const CardArea = styled.div`
  display: flex;
  overflow-x: none;
  flex-wrap: wrap;
  justify-content: flex-start;

`;
const Card = styled.div`
  background: #20a3fa;
  width: 265px;
  height: 170px;
  display: block;
  &:hover {
    cursor: pointer;
  }
`;
const Img = styled.img `
  position: relative;
  z-index: 2;
  height: 100%;
  width: 100%;
  transition-duration: 0.4s;
  -webkit-transition-duration: 0.4s;
  &:hover {
    opacity: 0.2;
    filter: blur(8px);
    -webkit-filter: blur(4px);
  }
`;
const CardTitle = styled.div `
  z-index: 1;
  position: relative;
  top: -80%;
  font-size: 36px;
  font-weight: 800;
  color: white;
`;
const CardDescription = styled.div `
  z-index: 1;
  width: 90%;
  margin: auto;
  position: relative;
  top: -70%;
  font-size: 20px;
  color: white;
`;

function Cards() {  
  return (
    <CardArea>

        <Card>
          <Img src={whattowear}/>
          <CardTitle>WhatToWear</CardTitle>
          <CardDescription>
          The most fail-safe weather app.
          </CardDescription>
        </Card>
        <Card>
          <Img src={whattowear}/>
          <CardTitle>WhatToWear</CardTitle>
          <CardDescription>
          The most fail-safe weather app.
          </CardDescription>
        </Card>
        <Card>
          <Img src={whattowear}/>
          <CardTitle>WhatToWear</CardTitle>
          <CardDescription>
          The most fail-safe weather app.
          </CardDescription>
        </Card>


    </CardArea>
    
  );
}

export default Cards;
