import React from 'react';
import styled from '@emotion/styled';
import img from '../Images/whattowear.png';

const Body = styled.div `
  margin: 0px auto 0 auto;
  padding: 0 0 1000px 0;
  width: 100%;
  padding 50px 20px;
  overflow: visible;
  max-width: 900px;
`;
const ProjectTitle = styled.h1 `
  font-size: 48px;

`;
const ProjectTools = styled.div `
  font-size: 22px;
  font-weight: regular;
  font-style: italic;
`;
const ProjectHighlights = styled.div `
  font-size: 22px;
  font-weight: regular;
  font-style: italic;
`;
const Buttons = styled.ul `
  list-style-type: none;
  margin: 40px 0 0 0;
  overflow: visible;
  text-align: center;
`;
const HotButton = styled.button `
  text-decoration: none;
  border-style: solid;
  border-color: white;
  border-width: 4px;
  border-radius: 8px;

  display: inline-block;
  padding: 30px 50px;
  font-size: 22px;
  color: white;
  background-color: #1c1c1c;
  width: 200px;
  margin: 20px 40px 0 0px;
  transition-duration: 0.4s;
  -webkit-transition-duration: 0.4s;
  &:hover {
    cursor: pointer;
    border-color: #20a3fa;
    color: black;
    background-color: white;
  }
`;
const ProjectAbout = styled.h2 `
  font-size: 36px;

`;
const ProjectAboutText = styled.div `
  font-size: 24px;
`;
const Link = styled.a `
  text-decoration: none;
  color: #20a3fa;
  &:hover {
    text-decoration: underline;
  }
`;
const Img = styled.img `
  position: relative;
  z-index: 2;
  height: 100%;
  width: 100%;
  border-radius: 8px;
`;
function WhatToWear() {  
  return (
    <Body>
        <ProjectTitle>What To Wear</ProjectTitle>
        <ProjectTools>HTML5 - CSS3 - Javascript</ProjectTools><br/>
        <ProjectHighlights>API Calls - Web-hosting - Geolocation</ProjectHighlights><br/>
        <Img src={img}/>
        <Buttons>
            <a href="https://www.gregoryblood.me/weatherapi/" target="_blank"><HotButton>Website</HotButton></a>
            <a href="https://github.com/gregoryblood/gregoryblood.github.io/tree/master/weatherapi" target="_blank"><HotButton>GitHub</HotButton></a>
        </Buttons>
        <ProjectAbout>About</ProjectAbout>
        <ProjectAboutText>
          This was a quick project to introduce me to API's. I wanted to 
          create something that I would actually use. What better application
          for me than a weather app that takes less than 2 seconds to read.
          <br/><br/>
          I decided all an app would need to do is tell me what clothes to wear for 
          the given weather, rather than having me decipher temperatures throughout the day.
          <br/><br/>
          This app simply says in one sentance the optimal clothes (for me) to wear. It would 
          take the user's location, fetch API calls from <Link href='https://openweathermap.org/'>OpenWeatherMap.org</Link>, and than compute 
          the sentance. Since it rains here a lot, it will tell the reader to bring a raincoat 
          if it rains at any point during the day.
          </ProjectAboutText>
    </Body>
    
  );
}

export default WhatToWear;
