import styled from '@emotion/styled'

export const Body = styled.div `
    position: relative;
    margin: 40px auto 0 auto;
    padding: 0 0 200px 0;
    width: 100%;
    background: white;
    overflow: visible;
    max-width: 900px;
`;
export const TitleBar = styled.div `
    width: 100%;
    background: #20a3fa;
`;
export const PTitle = styled.div `
    font-size: 54px;
    font-weight: 800;
    color: white;
    padding: 20px;
`;
export const Header = styled.div `
    font-weight: 800;
    font-size: 36px;
`;
export const Flavor = styled.div `
    padding: 10px 0;
    font-size: 24px;
    font-style: italic;
    color: white;
    padding: 0px 20px;
`;
export const Portal = styled.iframe `
    margin: 10px auto;
    padding: 0;
    display: block;
    width: 100%;
    max-width: 800px;
    border-style: none;
    border-radius: 8px;
`;
