import { NavLink } from 'react-router-dom';

const CardArea = styled.div`
  display: flexbox;
  flex-wrap: wrap;
  text-align: center;
  width: 100%;
  max-width: 1200px;
  align-items: center;

`;
const Card = styled(NavLink)`
  text-decoration: none;
  background: transparent;
  width: 300px;
  height: 200px;
  display: block;
  &:hover {
    cursor: pointer;
  }
`;
const Img = styled.img `
  position: relative;
  z-index: 2;
  height: 100%;
  width: 100%;
  margin: 0;
  padding: 0;
  transition-duration: 0.4s;
  -webkit-transition-duration: 0.4s;
  &:hover {
    opacity: 0.35;
    filter: blur(8px);
    -webkit-filter: blur(4px);
  }
  @media(max-width: 768px) {
    opacity: 0.35;
    filter: blur(8px);
    -webkit-filter: blur(4px);
  }
`;
const CardTitle = styled.div `
  z-index: 1;
  position: relative;
  top: -80%;
  font-size: 28px;
  font-weight: 800;
  color: black;
`;
const CardDescription = styled.div `
