import React from 'react';
import styled from '@emotion/styled'

const TitleDiv = styled.div `
  z-index: -1;
  position: fixed;
  color: white;
  background: rgb(0,145,255);
  background: linear-gradient(135deg, rgba(0,145,255,1) 0%, rgba(222,45,255,1) 50%, rgba(255,45,0,1) 100%);
  height: 100vh;
  width: 100vw;
  font-family: 'Montserrat', sans-serif;
  display: inline-block;
  font-weight: 400;
  line-height: 1;

`;
const TitleWords = styled.div `
  position: absolute;
  bottom: 2vw;
  left: 2vw;
`;
const Name = styled.div `
  margin-top: 100px;
  display: inline;
  font-size: 18vw;
`;
const Cursor = styled.div `
  display: inline;
  font-size: 21vw;
  font-weight: 200;
  animation:blinkingText 1.2s infinite;
  @keyframes blinkingText{
    0%{     color: white;    }
    49%{    color: white; }
    60%{    color: transparent; }
    99%{    color:transparent;  }
    100%{   color: white;    }
  }

`;

function Title() {
  return (
    <React.Fragment>
      <TitleDiv>
        <TitleWords>
          <Name>Gregory Blood</Name>
          <Cursor>|</Cursor>
        </TitleWords>
      </TitleDiv>


    </React.Fragment>
  );
}

export default Title;
