import React from 'react';
import styled from '@emotion/styled'
import whattowear from '../Images/whattowear.png'
const Core = styled.div `
  position: relative;
  top: 100vh;
  margin: auto;
  width: 100vw;
  background: rgb(233,243,251);
  background: linear-gradient(135deg, rgba(233,243,251,1) 0%, rgba(223,235,246,1) 100%);
  border-radius: 40px;
`;
const Section = styled.div `
  padding: 20px; 

  margin: 0 auto 40px auto;
  padding 100px 0 0 0;
  overflow: visible;

  max-width: 800px;
`;
const Header = styled.div `
  font-weight: 800;
  font-size: 4vw;
`;
const Flavor = styled.div `
  font-size: 2vw;
  font-style: italic;
  color: gray;
`;
const Text = styled.div `
  font-size: 3vw;
`;
const CardArea = styled.div `

  display: flex;
  flex-wrap: wrap;
  text-align: center;
  justify-content: center;
`;
const Card = styled.div `
  border-radius: 50px;
  background: #e6f2fc;
  box-shadow:  20px 20px 60px #bec8d1, 
              -20px -20px 60px #ffffff;
  margin: 20px 20px 20px 20px;
  padding: 20px;
  width: 25vw;
  min-width: 380px;
`;
const Img = styled.img `
  border-top-left-radius: 40px;
  border-top-right-radius: 40px;
  width: 100%;
`;
const CardTitle = styled.div `
  font-size: 4vw;
  padding: 0 10px 10px 10px;
`;
const CardDescription = styled.div `
  padding: 0 10px 15px 10px;
`;
function Body() {


  return (
    <Core>
      <Section>
        <Header>Who am I?</Header>
        <Flavor>Good question... Do we ever really know?</Flavor>
        <Text>
          I am a Senior at Oregon State University studying 
          Computer science. I specialize in Web and Cloud 
          Development but in my free time time I create websites 
          and games.
        </Text>
      </Section>
      <Section>
        <Header>What have I worked on?</Header>
        <Flavor>Bit of this, byte of that...</Flavor>
        <Text>
        </Text>
      </Section>
      <CardArea>
        <Card>
          <Img src={whattowear}/>
          <CardTitle>WhatToWear</CardTitle>
          <CardDescription>
          The most fail-safe weather app.
          </CardDescription>
        </Card>
        <Card>
          <Img src={whattowear}/>
          <CardTitle>WhatToWear</CardTitle>
          <CardDescription>
          The most fail-safe weather app.
          </CardDescription>
        </Card>
        <Card>
          <Img src={whattowear}/>
          <CardTitle>WhatToWear</CardTitle>
          <CardDescription>
          The most fail-safe weather app.
          </CardDescription>
        </Card>

      </CardArea>

    </Core>
  );
}

export default Body;
