import React from 'react';
import styled from '@emotion/styled';
import Cards from './Cards';
import Navbar from './Navbar';

const color = 'white';
const mainText = 'black';
const flavorText = "gray"
const flavorColor = "#20a3fa"

const Core = styled.div `
  position: relative;
  top: 100vh;
  margin: 40px auto 0 auto;
  padding: 0 0 1000px 0;
  width: 100%;
  font-weight: 600px;
  background: ${color};
  box-shadow: 8px 0 16px 0 rgba(0,0,0,0.6);
  border-top-left-radius: 25px;
`;
const Section = styled.div `
  margin: 40px auto 40px auto;
  padding 50px 50px;
  overflow: visible;
  max-width: 900px;
  border-radius: 40px;
  color: ${mainText};

`;
const Header = styled.div `
  font-weight: 800;
  font-size: 36px;
  
`;
const Flavor = styled.div `
  padding: 10px 0;
  font-size: 20px;
  font-style: italic;
  color: ${flavorText};
`;
const Text = styled.div `
  font-size: 24px;
`;
const CardArea = styled.div `
  display: flex;
  flex-wrap: wrap;
  text-align: center;
  justify-content: space-evenly;
  margin: 0 auto 40px auto;
  width: 100vw;
  max-width: 800px;
`;
const Link = styled.a `
  text-decoration: none;
  color: ${flavorColor};
  &:hover {
    text-decoration: underline;
  }
`;
const Buttons = styled.ul `
  list-style-type: none;
  margin: 0;
  padding: 0;
  overflow: visible;
  text-align: center;
`;
const HotButton = styled.button `
  text-decoration: none;
  border-style: solid;
  border-color: white;
  border-width: 4px;
  overflow: visible;
  display: inline-block;
  padding: 20px 40px;
  text-align: center;
  font-size: 20px;
  color: white;
  background-color: #1c1c1c;
  margin: 80px 40px;
  &:hover {
    cursor: pointer;
    border-color: ${flavorColor};
    color: ${flavorColor};
  }
`;

function Body() {  
  return (
    <React.Fragment>
      <span id="top"/>
      <Core>
        <Navbar/>
        <Section id="about">
          <Header>Who am I?</Header>
          <Flavor>Good question... It seems to be always changing</Flavor>
          <Text>
            I am a Senior at <Link href={'https://oregonstate.edu/'} target={'_blank'}>Oregon State University</Link> studying 
            Computer science.<br/><br/>
            I'm eager to work with teams to solve a given problem and am excited to be able to express my mixture of creativity and efficiency.
            <br/><br/>Check out my resume for some of the technologies I've been working with.
          </Text>
          <Buttons>
            <HotButton>Resume</HotButton>
            <HotButton>LinkedIn</HotButton>
            <HotButton>GitHub</HotButton>
          </Buttons>
        </Section>
        <Section id="projects">
          <Header>What have I worked on?</Header>
          <Flavor>Bit of this, a byte of that...</Flavor>

          <CardArea>
          <Cards/>
          
        </CardArea>
        </Section>
      </Core>
    </React.Fragment>
    
  );
}

export default Body;
