import React from 'react';
import styled from '@emotion/styled';
import { css } from '@emotion/core';
import { Link } from "react-scroll";
const TitleDiv = styled.div `
  position: fixed;
  color: white;
  top: 0px;
  height: 100vh;
  width: 100%;
  display: inline-block;
  font-weight: 400;
  background-color: black;  
  text-decoration:none;
`;

const Terminal = styled.div`

`;
const Dr = styled.a`
  color: #5770ff;
  display: block;
  width: 200px;
  &:hover {
    cursor: pointer;
    text-decoration: underline;
  }
`;
const File = styled.a`
  margin-right: 40px;
  color: #86ff57;
  &:hover {
    cursor: pointer;
    text-decoration: underline;
  }
`;
const Words = styled.div`
  font-size: 28px;
  margin: 0 20px;
  
`;
const Grid = styled.div `
  width: 600px;
  display: flex;
  flex-wrap: wrap;

`;
const TopBar = styled.div`
  background-color: #2d2d2d;
  width: 100%;
  height: 40px;
`;

const dynamicStyle = props => 
  css`
    background-color: ${props.color};
  `;

const Dot = styled.span`
  height: 30px;
  width: 30px;
  border-radius: 50%;
  display: inline-block;
  margin: 6px;
  ${dynamicStyle};
`;

function Title() {
  return (
    <React.Fragment>
      <TitleDiv>
        <Terminal>
          <TopBar>
            <Dot color="#ff4036"/>
            <Dot color="#e5c02a"/>
            <Dot color="#55c22a"/>
          </TopBar>
          <Words>
            <p>Last Login: Wed Feb 29 22:31:52 on console</p>
            <br/>
            <p>Gregorys-MacBook-Pro:~ gregoryblood$ ls </p>
            <Grid>
              <Link
                      activeClass="active"
                      to="about"
                      spy={true}
                      smooth={true}
                      offset={-70}
                      duration={500}>
              <Dr>about</Dr>
              </Link>
              <Link
                      activeClass="active"
                      to="projects"
                      spy={true}
                      smooth={true}
                      offset={-70}
                      duration={500}>
              <Dr>projects</Dr>
              </Link>
              <Link
                      activeClass="active"
                      to="info"
                      spy={true}
                      smooth={true}
                      offset={-70}
                      duration={500}>
              <Dr>contact</Dr>
              </Link>
              <File>resume.pdf</File>
              <File>e-mail.exe</File>
              
            
            </Grid>
            <br/>
            <p>Gregorys-MacBook-Pro:~ gregoryblood$ </p>
          

          </Words>
        </Terminal>

      </TitleDiv>
      
      
    </React.Fragment>
  );
}

export default Title;
