import React, {useEffect} from 'react';
import styled from '@emotion/styled';
import { Link } from "react-scroll";

const List = styled.ul `
    background-color: #1c1c1c;
    margin: 0;
    padding: 0;
    overflow: hidden;
    position: fixed;
    top: 0;
    width: 100%;
    box-shadow: 0 8px 16px 0 rgba(0,0,0,0.6);
    
`;

const Item = styled.li`
    text-decoration: none;
    float: left;
    display: block;
    color: white;
    text-align: center;
    font-size: 26px;
    padding: 28px 32px;
    text-decoration: none;
    &:hover {
        background-color: lightblue;
        color: black;
        cursor: pointer;
    }
`;

const Navbar=() => {
    const [scrolled,setScrolled]=React.useState(false);
    const handleScroll=() => {
        const offset=window.scrollY;
        if(offset > 200 ){
        setScrolled(true);
        }
        else{
        setScrolled(false);
        }
    }

    useEffect(() => {
        window.addEventListener('scroll',handleScroll)
    })
    let navbarClasses=['navbar'];
    if(scrolled){
        navbarClasses.push('scrolled');
    }

   return (
    <React.Fragment>
            <List>
                    <Link
                    activeClass="active"
                    to="about"
                    spy={true}
                    smooth={true}
                    offset={-70}
                    duration={500}>
                    <Item>
                        About
                    </Item>
                    </Link>
                <Item>
                    <Link
                    activeClass="active"
                    to="projects"
                    spy={true}
                    smooth={true}
                    offset={-70}
                    duration={500}>
                        Projects
                    </Link>
                </Item>
                <Item>
                    <Link
                    activeClass="active"
                    to="info"
                    spy={true}
                    smooth={true}
                    offset={-70}
                    duration={500}>
                        Info
                    </Link>
                </Item>
            </List>
        </React.Fragment>
   )
};

export default Navbar;
