import React from 'react';
import styled from '@emotion/styled'
import whattowear from '../Images/whattowear.png'

const color = '#edf2f7';

const Core = styled.div `
  position: relative;
  top: 100vh;
  margin: auto;
  width: 100vw;
  background: ${color};

  border-radius: 40px;
`;
const Section = styled.div `

  margin: 0 auto 40px auto;
  padding 100px 20px 20px 20px;
  overflow: visible;

  max-width: 800px;

  border-radius: 40px;
`;
const Header = styled.div `
  font-weight: 800;
  font-size: 48px;
`;
const Flavor = styled.div `
  font-size: 36px;
  font-style: italic;
  color: gray;
`;
const Text = styled.div `
  font-size: 36px;
`;
const CardArea = styled.div `
  display: flex;
  flex-wrap: wrap;
  text-align: center;
  justify-content: flex-start;
  margin: 0 auto 40px auto;
  max-width: 800px;
`;
const Card = styled.div `
  border-radius: 40px;
  background: ${color};
  box-shadow:  9px 9px 18px #bbc5cf, 
              -9px -9px 18px #ffffff;
  margin: 20px 20px 20px 20px;
  padding: 20px;
  width: 100vw;
  max-width: 800px;

  &:hover {
    box-shadow: inset 9px 9px 18px #bbc5cf, 
                inset -9px -9px 18px #ffffff;
  }
`;
const Img = styled.img `
  border-top-left-radius: 40px;
  border-top-right-radius: 40px;
  width: 100%;
`;
const CardTitle = styled.div `
  font-size: 4vw;
  padding: 0 10px 10px 10px;
`;
const CardDescription = styled.div `
  padding: 0 10px 15px 10px;
`;
function Body() {


  return (
    <Core>
      <Section>
        <Header>Who am I?</Header>
        <Flavor>Good question... Do we ever really know?</Flavor>
        <Text>
          I am a Senior at Oregon State University studying 
          Computer science. I specialize in Web and Cloud 
          Development but in my free time time I create websites 
          and games.
        </Text>
      </Section>
      <Section>
        <Header>What have I worked on?</Header>
        <Flavor>Bit of this, byte of that...</Flavor>
        <Text>
        </Text>
      </Section>
      <CardArea>
        <Card>
          <Img src={whattowear}/>
          <CardTitle>WhatToWear</CardTitle>
          <CardDescription>
          The most fail-safe weather app.
          </CardDescription>
        </Card>
        <Card>
          <Img src={whattowear}/>
          <CardTitle>WhatToWear</CardTitle>
          <CardDescription>
          The most fail-safe weather app.
          </CardDescription>
        </Card>
        <Card>
          <Img src={whattowear}/>
          <CardTitle>WhatToWear</CardTitle>
          <CardDescription>
          The most fail-safe weather app.
          </CardDescription>
        </Card>

      </CardArea>

    </Core>
  );
}

export default Body;
