import React from 'react';
import styled from '@emotion/styled'
import whattowear from '../Images/whattowear.png'
import { Switch,
  Route,
  Link,
  NavLink,
} from 'react-router-dom';

const CardBorder = styled.div `
  padding: 0.2rem;
  position: relative;
  width: 100vw;
  max-width: 350px;
  transition-duration: 0.8s;
  -webkit-transition-duration: 0.8s;
  background: white;
  &:hover {
    background-image: linear-gradient(to right bottom, #4fe8ff, #00caff, #00a7ff, #007eff, #0043ff);    box-shadow: 0 8px 16px 0 rgba(0,0,0,0.8);
  }
`;
const CardArea = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: flex-start;
`;
const Card = styled.div`
  background: white;
  transition-duration: 0.8s;
  -webkit-transition-duration: 0.8s;

  &:hover {
    //color: white;
    //background: transparent;
  }
`;
const Img = styled.img `
  z-index: -1;

  width: 100%;
  &:hover {
    opacity: 0.5;
    filter: blur(8px);
    -webkit-filter: blur(8px);
    z-index: -5;
  }
`;
const CardTitle = styled.div `
  z-index: -1;

  position: absolute;
  font-size: 36px;
  top: 30%;
  left: 50%;
  transform: translate(-50%, -50%);
`;
const CardDescription = styled.div `
  z-index: -1;

  width: 100%;
  font-size: 24px;
  color: #6e6e6e;
  position: absolute;
  top: 70%;
  left: 50%;
  transform: translate(-50%, -50%);
`;

function Cards() {  
  return (
    <CardArea>
      <CardBorder>
        <Card>
          <Img src={whattowear}/>
          <CardTitle>WhatToWear</CardTitle>
          <CardDescription>
          The most fail-safe weather app.
          </CardDescription>
        </Card>
      </CardBorder>
      <CardBorder>
        <Card>
          <Img src={whattowear}/>
          <CardTitle>WhatToWear</CardTitle>
          <CardDescription>
          The most fail-safe weather app.
          </CardDescription>
        </Card>
      </CardBorder>
      <CardBorder>
        <Card>
          <Img src={whattowear}/>
          <CardTitle>WhatToWear</CardTitle>
          <CardDescription>
          The most fail-safe weather app.
          </CardDescription>
        </Card>
      </CardBorder>
    </CardArea>
    
  );
}

export default Cards;
