import React from 'react';
import {PTitle, Body, Core, Header, Flavor, Portal, TitleBar,
        Tools, Text, Img} from './style';
import {Buttons, HotButton} from '../Components/Body';

export class MasterBlaster extends React.Component {  
  componentDidMount() {
    window.scrollTo(0, 0)
  };
  render() {
    return (
      <React.Fragment>
        <span id="top"/>
        <TitleBar>
          <PTitle>Master Blaster</PTitle>
          <Flavor>A retro game with a twist...</Flavor>
        </TitleBar>
        <Core>
          <Body>
          <div id="unity-container" class="unity-desktop">
      <canvas id="unity-canvas"></canvas>
      <div id="unity-loading-bar">
        <div id="unity-logo"></div>
        <div id="unity-progress-bar-empty">
          <div id="unity-progress-bar-full"></div>
        </div>
      </div>
      <div id="unity-mobile-warning">
        WebGL builds are not supported on mobile devices.
      </div>
      <div id="unity-footer">
        <div id="unity-webgl-logo"></div>
        <div id="unity-fullscreen-button"></div>
        <div id="unity-build-title">MasterBlaster</div>
      </div>
    </div>
            <Buttons>
              <a href='https://gregoryblood.itch.io/master-blaster' target='_blank'><HotButton>Visit</HotButton></a>  
              {
              //<a href='https://github.com/gregoryblood/' target='_blank'><HotButton>GitHub</HotButton></a>
              }
            </Buttons>
            <Header>
              What I Learned
            </Header>
            <Tools>Developing with a Time Limit - Developing with a Theme</Tools>
            <Text>

            </Text>

            <Header>
              About
            </Header>
            <Tools>Unity - C#</Tools>
            <Text>

            </Text>

          </Body>
        </Core>
      </React.Fragment>
    );
  };
}

export default MasterBlaster;
