import React from 'react';
import styled from '@emotion/styled';
import { css } from '@emotion/core';
import { Link } from "react-scroll";
const TitleDiv = styled.div `
  position: fixed;
  color: white;
  top: 0px;
  height: 100vh;
  width: 100%;
  display: inline-block;
  font-weight: 400;
  background-color: black;  
  text-decoration:none;
`;

const Terminal = styled.div`

`;
const Dr = styled.a`
  color: #5770ff;
  display: block;
  width: 200px;
  height: 50px;
  &:hover {
    cursor: pointer;
    text-decoration: underline;
  }
`;
const File = styled.a`
  margin-right: 40px;
  color: #86ff57;
  &:hover {
    cursor: pointer;
    text-decoration: underline;
  }
`;
const Words = styled.div`
  line-height: 50%;
  font-size: 28px;
  margin: -10px 20px;
  font-family: 'Source Code Pro', monospace;
`;
const Grid = styled.div `
  width: 600px;
  display: flex;
  flex-wrap: wrap;

`;
const TopBar = styled.div`
  background-color: #2d2d2d;
  width: 100%;
  height: 30px;
`;
const TextBox = styled.input `
  width: 20%;
  color: white;
  background-color: black;
  font-size: 28px;
  font-family: 'Source Code Pro', monospace;
  border: none;
`;

const dynamicStyle = props => 
  css`
    background-color: ${props.color};
  `;

const Dot = styled.span`
  height: 20px;
  width: 20px;
  border-radius: 50%;
  display: inline-block;
  margin: 6px;
  ${dynamicStyle};
`;

const Title = React.createClass({
  getInitialState() {
    return {
      now: new Date(),
    };
    this.interval = null;
  },

  componentDidMount: function() {
    const self = this;
    self.interval = setInterval(function() {
      self.setState({
        now: new Date(),
      });
    }, 1000);
  },

  componentWillUnmount: function() {
    clearInterval(this.interval);
  },
  render() {
    return (
    <React.Fragment>
      <TitleDiv>
        <Terminal>
          <TopBar>
            <Dot color="#ff4036"/>
            <Dot color="#e5c02a"/>
            <Dot color="#55c22a"/>
          </TopBar>
          <Words>
          <Time value={this.state.now} format="DD/MM/YYYY" />
            <p>Last Login: Wed Feb 29 22:31:52 on console</p>
            <p>Gregorys-MacBook-Pro:~ gregoryblood$ ls </p>
            <Grid>
              <Link
                      activeClass="active"
                      to="about"
                      spy={true}
                      smooth={true}
                      offset={-70}
                      duration={500}>
              <Dr>about</Dr>
              </Link>
              <Link
                      activeClass="active"
                      to="projects"
                      spy={true}
                      smooth={true}
                      offset={-70}
                      duration={500}>
              <Dr>projects</Dr>
              </Link>
              <Link
                      activeClass="active"
                      to="info"
                      spy={true}
                      smooth={true}
                      offset={-70}
                      duration={500}>
              <Dr>contact</Dr>
              </Link>
              <File>resume.pdf</File>
              <File>e-mail.exe</File>
            
            </Grid>
            <br/>
            <p>Gregorys-MacBook-Pro:~ gregoryblood$ <TextBox></TextBox></p>
            

          </Words>
        </Terminal>

      </TitleDiv>
      
      
    </React.Fragment>
  );
  }
});

export default Title;
